//Vertical Gaussian Blur
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 pixel = 1.0/iChannelResolution[0].xy;
    vec2 uv = fragCoord.xy * pixel;

    float vertical = pixel.y;
    vec4 sumatory = vec4(0.0,0.0,0.0,0.0);

	float firstMultiply = 0.05;
	float secondMultiply = 0.16;
    float normalization = 0.98;

    for(float i = 4.0; i > 0.0; i--){
        if(i == 4.0){
        	firstMultiply = firstMultiply;
        }

        if(i == 3.0){
        	firstMultiply += 0.04;
        }


        if(i == 2.0){
        	firstMultiply += 0.03;
        }

        if(i == 1.0){
        	firstMultiply += 0.03;
        }

        sumatory += texture(iChannel0, fract(vec2(uv.x,uv.y - i*vertical)))*firstMultiply;
    } 

    for(float j = 0.0; j <= 4.0; j++){
        if(j == 0.0){
			secondMultiply = secondMultiply;
        }

        if(j == 1.0){
			secondMultiply -= 0.01;
        }

        if(j == 2.0){
			secondMultiply -= 0.03;
        }

        if(j == 3.0){
			secondMultiply -= 0.03;
        }

        if(j == 4.0){
			secondMultiply -= 0.04;
        }

        sumatory += texture(iChannel0, fract(vec2(uv.x,uv.y+j*vertical)))*secondMultiply;
    }


    fragColor.xyz = sumatory.xyz/normalization; 
	fragColor.a = 1.0;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy;
    vec3 textureColor = vec3(texture(iChannel0,uv).xyz);

    fragColor = vec4(textureColor, 1.0 );
}

