// tracing stuff: line ~900 (tweak mainImage NUM_SAMPLES for quality)
// shading stuff: line ~850 (tweak ImageBasedLightingMIS NumSamples for quality)

const float M_PI = 3.14159265358979323846;
const float M_PHI = 1.6180339887498948482045868343656;
const float FLT_EPSILON = 9.99999974737875E-06;
const float FLT_MAX = 3.402823466e+38F;

float unorm(float v) {
	return v * 0.5 + 0.5;
}
vec2 unorm(vec2 v) {
	return v * 0.5 + 0.5;
}
vec3 unorm(vec3 v) {
	return v * 0.5 + 0.5;
}
float snorm(float v) {
    return v * 2.0 - 1.0;
}
vec2 snorm(vec2 v) {
	return v * 2.0 - 1.0;
}
vec3 snorm(vec3 v) {
	return v * 2.0 - 1.0;
}

float mincomp(vec2 v) { return min(v.x, v.y); }
float maxcomp(vec2 v) { return max(v.x, v.y); }
float mincomp(vec3 v) { return min(min(v.x, v.y), v.z); }
float maxcomp(vec3 v) { return max(max(v.x, v.y), v.z); }

float smin(float a, float b, float k) {
    // https://iquilezles.org/articles/smin
    float res = exp(-k * a) + exp(-k * b);
    return -log(res) / k;
}

float cubemap_layerface(float layer_orig) {
	float layer = floor(layer_orig / 6.0);
	float face = layer_orig - (layer * 6.0);
	return face;
}

float sFract(float x, float sm){ float fx = fract(x); return min(fx, fx*(1. - fx)/fwidth(x)/sm); }
// Only correct for nonnegative values, but in this example, numbers aren't negative.
float sFloor(float x){ return x - sFract(x, 1.); } 

void swap(inout float a, inout float b) {
	float t = a;
	a = b;
	b = t;
}
float gamma(float n) {
	return (n * FLT_EPSILON) / (1.0 - n * FLT_EPSILON);
}

//==========================================================================================
// indexing
//==========================================================================================

int index2d(ivec2 p, ivec2 bounds) {
	int index = p.x +
				p.y * bounds.x;
	return index;
}
int index3d(ivec3 p, ivec3 bounds) {
	int index = p.x +
				p.y * bounds.x +
				p.z * bounds.x * bounds.y;
	return index;
}
int index4d(ivec4 p, ivec4 bounds) {
	int index = p.x +
				p.y * bounds.x +
				p.z * bounds.x * bounds.y +
				p.w * bounds.x * bounds.y * bounds.z;
	return index;
}

//==========================================================================================
// hashes
//==========================================================================================

float hash1(vec2 p) {
	p = 50.0*fract(p*0.3183099);
	return fract(p.x*p.y*(p.x + p.y));
}

float hash1(float n) {
	return fract(n*17.0*fract(n*0.3183099));
}

vec2 hash2(float n) { return fract(sin(vec2(n, n + 1.0))*vec2(43758.5453123, 22578.1459123)); }

vec2 hash2(vec2 p) {
	const vec2 k = vec2(0.3183099, 0.3678794);
	p = p*k + k.yx;
	return fract(16.0 * k*fract(p.x*p.y*(p.x + p.y)));
}

//==========================================================================================
// rng
//==========================================================================================

// http://stackoverflow.com/questions/4200224/random-noise-functions-for-glsl
// A single iteration of Bob Jenkins' One-At-A-Time hashing algorithm.
uint hash(uint x) {
	x += (x << 10u);
	x ^= (x >> 6u);
	x += (x << 3u);
	x ^= (x >> 11u);
	x += (x << 15u);
	return x;
}
// Compound versions of the hashing algorithm I whipped together.
uint hash(uvec2 v) { return hash(v.x ^ hash(v.y)); }
uint hash(uvec3 v) { return hash(v.x ^ hash(v.y) ^ hash(v.z)); }
uint hash(uvec4 v) { return hash(v.x ^ hash(v.y) ^ hash(v.z) ^ hash(v.w)); }

// Construct a float with half-open range [0:1] using low 23 bits.
// All zeroes yields 0.0, all ones yields the next smallest representable value below 1.0.
float floatConstruct(uint m) {
	const uint ieeeMantissa = 0x007FFFFFu;	// binary32 mantissa bitmask
	const uint ieeeOne = 0x3F800000u;		// 1.0 in IEEE binary32

	m &= ieeeMantissa;						// Keep only mantissa bits (fractional part)
	m |= ieeeOne;							// Add fractional part to 1.0

	float  f = uintBitsToFloat(m);			// Range [1:2]
	return f - 1.0;							// Range [0:1]
}

//uniform uvec4 random_seed;
//uvec4 seed = random_seed;
uvec4 seed = uvec4(0);
void random_init1ui(uint x) {
	seed ^= uvec4(x, ~x, -x, -~x);
}
void random_init2ui(uvec2 x) {
	seed ^= uvec4(x.x, x.y, ~x.x, ~x.y);
}
void random_init2f(vec2 v) {
	uvec2 x = floatBitsToUint(v);
	seed ^= uvec4(x.x, x.y, ~x.x, ~x.y);
}
float random1f() {
	return floatConstruct(hash(seed++));
}
vec2 random2f() {
	return vec2(random1f(), random1f());
}
vec3 random3f() {
	return vec3(random1f(), random1f(), random1f());
}
float normal1f() {
	return sqrt(random1f()) * cos(random1f() * M_PI * 2.0);
}
vec2 normal2f() {
	return vec2(normal1f(), normal1f());
}

//==========================================================================================
// encoding
//==========================================================================================

// https://www.shadertoy.com/view/4t2XWK
float madfrac(float a, float b) { return a*b - floor(a*b); }
vec2  madfrac(vec2 a, float b) { return a*b - floor(a*b); }

float encode_normal(vec3 p, float n) {
	float phi = min(atan(p.y, p.x), M_PI), cosTheta = p.z;

	float k = max(2.0, floor(log(n * M_PI * sqrt(5.0) * (1.0 - cosTheta*cosTheta)) / log(M_PHI*M_PHI)));
	float Fk = pow(M_PHI, k) / sqrt(5.0);

	vec2 F = vec2(round(Fk), round(Fk * M_PHI));

	vec2 ka = -2.0*F / n;
	vec2 kb = 2.0*M_PI*madfrac(F + 1.0, M_PHI - 1.0) - 2.0*M_PI*(M_PHI - 1.0);
	mat2 iB = mat2(ka.y, -ka.x, -kb.y, kb.x) / (ka.y*kb.x - ka.x*kb.y);

	vec2 c = floor(iB * vec2(phi, cosTheta - (1.0 - 1.0 / n)));
	float d = 8.0;
	float j = 0.0;
	for (int s = 0; s<4; s++)
	{
		vec2 uv = vec2(float(s - 2 * (s / 2)), float(s / 2));

		float cosTheta = dot(ka, uv + c) + (1.0 - 1.0 / n);

		cosTheta = clamp(cosTheta, -1.0, 1.0)*2.0 - cosTheta;
		float i = floor(n*0.5 - cosTheta*n*0.5);
		float phi = 2.0*M_PI*madfrac(i, M_PHI - 1.0);
		cosTheta = 1.0 - (2.0*i + 1.0) / n;
		float sinTheta = sqrt(1.0 - cosTheta*cosTheta);

		vec3 q = vec3(cos(phi)*sinTheta, sin(phi)*sinTheta, cosTheta);
		float squaredDistance = dot(q - p, q - p);
		if (squaredDistance < d)
		{
			d = squaredDistance;
			j = i;
		}
	}
	return j;
}

vec3 decode_normal(float i, float n) {
	float phi = 2.0*M_PI*madfrac(i, M_PHI);
	float zi = 1.0 - (2.0*i + 1.0) / n;
	float sinTheta = sqrt(1.0 - zi*zi);
	return vec3(cos(phi)*sinTheta, sin(phi)*sinTheta, zi);
}

//==========================================================================================
// sampling
//==========================================================================================

float radicalInverse_VdC(uint bits) {
	bits = (bits << 16u) | (bits >> 16u);
	bits = ((bits & 0x55555555u) << 1u) | ((bits & 0xAAAAAAAAu) >> 1u);
	bits = ((bits & 0x33333333u) << 2u) | ((bits & 0xCCCCCCCCu) >> 2u);
	bits = ((bits & 0x0F0F0F0Fu) << 4u) | ((bits & 0xF0F0F0F0u) >> 4u);
	bits = ((bits & 0x00FF00FFu) << 8u) | ((bits & 0xFF00FF00u) >> 8u);
	return float(bits) * 2.3283064365386963e-10; // / 0x100000000
}

vec2 hammersley2d(uint i, uint N) {
	return vec2(float(i) / float(N), radicalInverse_VdC(i));
}
vec2 hammersley2d(int i, int N) {
	return vec2(float(i) / float(N), radicalInverse_VdC(uint(i)));
}

// https://www.shadertoy.com/view/MsdGzl
vec3 cosineDirection(vec2 rnd, vec3 nor) {
	// compute basis from normal
	// see http://orbit.dtu.dk/fedora/objects/orbit:113874/datastreams/file_75b66578-222e-4c7d-abdf-f7e255100209/content
	// (link provided by nimitz)
	vec3 tc = vec3(1.0 + nor.z - nor.xy*nor.xy, -nor.x*nor.y) / (1.0 + nor.z);
	vec3 uu = vec3(tc.x, tc.z, -nor.x);
	vec3 vv = vec3(tc.z, tc.y, -nor.y);

	float u = rnd.x;
	float v = rnd.y;
	float a = M_PI * 2.0 * v;

	vec3 d = sqrt(u)*(cos(a)*uu + sin(a)*vv) + sqrt(1.0 - u)*nor;
	d = normalize(d);
	return d;
}

vec2 sampleSphericalMap(vec3 v) {
	const vec2 invAtan = vec2(0.1591, 0.3183);
    vec2 uv = vec2(atan(v.z, v.x), asin(v.y));
    uv *= invAtan;
    uv += 0.5;
    return uv;
}

//==========================================================================================
// distance
//==========================================================================================

vec2 sdTranslate(vec2 p, vec2 t) {
	return p - t;
}

float sdSmoothMerge(float d1, float d2, float k) {
    float h = clamp(0.5 + 0.5*(d2 - d1)/k, 0.0, 1.0);
    return mix(d2, d1, h) - k * h * (1.0-h);
}

float sdMerge(float d1, float d2) {
	return min(d1, d2);
}

float sdMergeExclude(float d1, float d2) {
	return min(max(-d1, d2), max(-d2, d1));
}

float sdSubstract(float d1, float d2) {
	return max(-d1, d2);
}

float sdIntersect(float d1, float d2) {
	return max(d1, d2);
}

float sdPie(vec2 p, float angle) {
	angle = radians(angle) / 2.0;
	vec2 n = vec2(cos(angle), sin(angle));
	return abs(p).x * n.x + p.y*n.y;
}

float sdCircle(vec2 p, float radius) {
	return length(p) - radius;
}


float sdTriangle(vec2 p, float radius) {
	return max(	abs(p).x * 0.866025 + 
			   	p.y * 0.5, -p.y) 
				-radius * 0.5;
}

float sdTriangle(vec2 p, float width, float height) {
	vec2 n = normalize(vec2(height, width / 2.0));
	return max(	abs(p).x*n.x + p.y*n.y - (height*n.y), -p.y);
}

float sdSemiCircle(vec2 p, float radius, float angle, float width) {
	width /= 2.0;
	radius -= width;
	return sdSubstract(sdPie(p, angle), 
					 abs(sdCircle(p, radius)) - width);
}

float sdBox(vec2 p, vec2 size, float radius) {
	size -= vec2(radius);
	vec2 d = abs(p) - size;
  	return min(max(d.x, d.y), 0.0) + length(max(d, 0.0)) - radius;
}

float sdLine(vec2 p, vec2 start, vec2 end, float width) {
	vec2 dir = start - end;
	float lngth = length(dir);
	dir /= lngth;
	vec2 proj = max(0.0, min(lngth, dot((start - p), dir))) * dir;
	return length( (start - p) - proj ) - (width / 2.0);
}

//==========================================================================================
// intersection
//==========================================================================================

float isect_sphere(vec3 ro, vec3 rd, vec4 sph) {
	vec3 oc = ro - sph.xyz;
	float b = dot(oc, rd);
	float c = dot(oc, oc) - sph.w*sph.w;
	float h = b*b - c;
	if (h<0.0) {
		return -1.0;
	}
	h = sqrt(h);
	return -b - h;
}

float isect_plane(vec3 ro, vec3 rd, vec4 p) {
	float t = -(dot(ro, p.xyz) + p.w) / dot(rd, p.xyz);
	return t;
}

float minT(float a, float b) {
	const float zfar = -1.0;
	return (a<b)? zfar: (b<0.)? (a>0.)? a: zfar: b;
}

// https://www.shadertoy.com/view/XtGXRz
float isect_aabb(vec3 ro, vec3 rd, vec3 mins, vec3 maxs) {
	vec3 t1 = (mins - ro)/rd; //https://www.siggraph.org/education/materials/HyperGraph/raytrace/rtinter3.htm
	vec3 t2 = (maxs - ro)/rd;
	vec3 tn = min(t1, t2);
	vec3 tx = max(t1, t2);
    float d = minT(mincomp(tx),maxcomp(tn)); //minT calculates the minimum positive, if n/a then returns zfar
	return d;
}

float isect_triangle( vec3 orig
                       , vec3 dir
                       , vec3 vert0
                       , vec3 vert1
                       , vec3 vert2
                       , out float u
                       , out float v
                       ) {
    // Fast, Minimum Storage Ray-Triangle Intersection
    //
    // Tomas MÃ¶ller and Ben Trumbore. Fast, minimum storage ray-triangle intersection.
    // Journal of graphics tools, 2(1):21-28, 1997
    //
    // http://www.jcenligne.fr/download/little3d/
    //     jgt%20Fast,%20Minumum%20Storage%20Ray-Triangle%20Intersection.htm

    const float JGT_RAYTRI_EPSILON = 0.000001;
	float t;

    vec3 edge1, edge2, tvec, pvec, qvec;
    float det, inv_det;

    // Find vectors for two edges sharing vert0
    edge1 = vert1 - vert0;
    edge2 = vert2 - vert0;

    // Begin calculating determinant - also used to calculate U parameter
    pvec = cross(dir, edge2);

    // If determinant is near zero, ray lies in plane of triangle
    det = dot(edge1, pvec);

    if (det > -JGT_RAYTRI_EPSILON && det < JGT_RAYTRI_EPSILON) {
        return -1.0;
	}
    inv_det = 1.0 / det;

    // Calculate distance from vert0 to ray origin
    tvec = orig - vert0;

    // Calculate U parameter and test bounds
    u = dot(tvec, pvec) * inv_det;
    if (u < 0.0 || u > 1.0) {
        return -1.0;
	}

    // Prepare to test V parameter
    qvec = cross(tvec, edge1);

    // Calculate V parameter and test bounds
    v = dot(dir, qvec) * inv_det;
    if (v < 0.0 || u + v > 1.0) {
        return -1.0;
	}

    // Calculate t, ray intersects triangle
    t = dot(edge2, qvec) * inv_det;

    return t;
}

//==========================================================================================
// pbr
//==========================================================================================

float DistributionGGX(vec3 N, vec3 H, float roughness) {
    float a = roughness*roughness;
    float a2 = a*a;
    float NdotH = max(dot(N, H), 0.0);
    float NdotH2 = NdotH*NdotH;

    float nom   = a2;
    float denom = (NdotH2 * (a2 - 1.0) + 1.0);
    denom = M_PI * denom * denom;

    return nom / denom;
}

float GeometrySchlickGGX(float NdotV, float roughness) {
    float r = (roughness + 1.0);
    float k = (r*r) / 8.0;

    float nom   = NdotV;
    float denom = NdotV * (1.0 - k) + k;

    return nom / denom;
}

float GeometrySmith(vec3 N, vec3 V, vec3 L, float roughness) {
    float NdotV = max(dot(N, V), 0.0);
    float NdotL = max(dot(N, L), 0.0);
    float ggx2 = GeometrySchlickGGX(NdotV, roughness);
    float ggx1 = GeometrySchlickGGX(NdotL, roughness);

    return ggx1 * ggx2;
}

vec3 FresnelSchlick(float cosTheta, vec3 F0) {
    return F0 + (1.0 - F0) * pow(1.0 - cosTheta, 5.0);
}

//==========================================================================================
// common
// https://github.com/EpicGames/UnrealEngine/blob/master/Engine/Shaders/Common.usf
//==========================================================================================

float ClampedPow(float X, float Y) {
	return pow(max(abs(X),0.000001f),Y);
}
float Pow5( float x ) {
	float xx = x*x;
	return xx * xx * x;
}

float Square(float X) {
	return X * X;
}
float rcp(float X) {
	return 1.0 / X;
}
float saturate(float x) {
	return clamp(x, 0.0, 1.0);
}
vec2 saturate(vec2 x) {
	return clamp(x, 0.0, 1.0);
}
vec3 saturate(vec3 x) {
	return clamp(x, 0.0, 1.0);
}

uint bitfieldReverse(uint bits) {
	bits = ( bits << 16) | ( bits >> 16);
	bits = ( (bits & 0x00ff00ffu) << 8u ) | ( (bits & 0xff00ff00u) >> 8u );
	bits = ( (bits & 0x0f0f0f0fu) << 4u ) | ( (bits & 0xf0f0f0f0u) >> 4u );
	bits = ( (bits & 0x33333333u) << 2u ) | ( (bits & 0xccccccccu) >> 2u );
	bits = ( (bits & 0x55555555u) << 1u ) | ( (bits & 0xaaaaaaaau) >> 1u );
	return bits;
}

//==========================================================================================
// monte carlo
// https://github.com/EpicGames/UnrealEngine/blob/master/Engine/Shaders/MonteCarlo.usf
//==========================================================================================

const float PI = M_PI;

vec3 TangentToWorld( vec3 Vec, vec3 TangentZ ) {
	vec3 UpVector = abs(TangentZ.z) < 0.999 ? vec3(0,0,1) : vec3(1,0,0);
	vec3 TangentX = normalize( cross( UpVector, TangentZ ) );
	vec3 TangentY = cross( TangentZ, TangentX );
	return TangentX * Vec.x + TangentY * Vec.y + TangentZ * Vec.z;
}

vec2 Hammersley( uint Index, uint NumSamples, uvec2 Random ) {
	float E1 = fract( float(Index) / float(NumSamples) + float( Random.x & 0xffffu ) / float(1<<16) );
	float E2 = float( bitfieldReverse(Index) ^ Random.y ) * 2.3283064365386963e-10;
	return vec2( E1, E2 );
}

vec4 UniformSampleSphere( vec2 E ) {
	float Phi = 2.0 * PI * E.x;
	float CosTheta = 1.0 - 2.0 * E.y;
	float SinTheta = sqrt( 1.0 - CosTheta * CosTheta );

	vec3 H;
	H.x = SinTheta * cos( Phi );
	H.y = SinTheta * sin( Phi );
	H.z = CosTheta;

	float PDF = 1.0 / (4.0 * PI);

	return vec4( H, PDF );
}

vec4 UniformSampleHemisphere( vec2 E ) {
	float Phi = 2.0 * PI * E.x;
	float CosTheta = E.y;
	float SinTheta = sqrt( 1.0 - CosTheta * CosTheta );

	vec3 H;
	H.x = SinTheta * cos( Phi );
	H.y = SinTheta * sin( Phi );
	H.z = CosTheta;

	float PDF = 1.0 / (2.0 * PI);

	return vec4( H, PDF );
}

vec2 UniformSampleDisk(vec2 Random) {
	float Theta = 2.0f * PI * Random.x;
	float Radius = sqrt(Random.y);
	return vec2(Radius * cos(Theta), Radius * sin(Theta));
}

vec4 CosineSampleHemisphere( vec2 E ) {
	float Phi = 2.0 * PI * E.x;
	float CosTheta = sqrt( E.y );
	float SinTheta = sqrt( 1.0 - CosTheta * CosTheta );

	vec3 H;
	H.x = SinTheta * cos( Phi );
	H.y = SinTheta * sin( Phi );
	H.z = CosTheta;

	float PDF = CosTheta / PI;

	return vec4( H, PDF );
}

vec4 UniformSampleCone( vec2 E, float CosThetaMax ) {
	float Phi = 2.0 * PI * E.x;
	float CosTheta = mix( CosThetaMax, 1.0, E.y );
	float SinTheta = sqrt( 1.0 - CosTheta * CosTheta );

	vec3 L;
	L.x = SinTheta * cos( Phi );
	L.y = SinTheta * sin( Phi );
	L.z = CosTheta;

	float PDF = 1.0 / ( 2.0 * PI * (1.0 - CosThetaMax) );

	return vec4( L, PDF );
}

vec4 ImportanceSampleBlinn( vec2 E, float Roughness ) {
	float m = Roughness * Roughness;
	float n = 2.0 / (m*m) - 2.0;

	float Phi = 2.0 * PI * E.x;
	float CosTheta = ClampedPow( E.y, 1.0 / (n + 1.0) );
	float SinTheta = sqrt( 1.0 - CosTheta * CosTheta );

	vec3 H;
	H.x = SinTheta * cos( Phi );
	H.y = SinTheta * sin( Phi );
	H.z = CosTheta;

	float D = (n+2.0)/ (2.0*PI) * ClampedPow( CosTheta, n );
	float PDF = D * CosTheta;

	return vec4( H, PDF );
}

vec4 ImportanceSampleGGX( vec2 E, float Roughness ) {
	float m = Roughness * Roughness;
	float m2 = m * m;

	float Phi = 2.0 * PI * E.x;
	float CosTheta = sqrt( (1.0 - E.y) / ( 1.0 + (m2 - 1.0) * E.y ) );
	float SinTheta = sqrt( 1.0 - CosTheta * CosTheta );

	vec3 H;
	H.x = SinTheta * cos( Phi );
	H.y = SinTheta * sin( Phi );
	H.z = CosTheta;

	float d = ( CosTheta * m2 - CosTheta ) * CosTheta + 1.0;
	float D = m2 / ( PI*d*d );
	float PDF = D * CosTheta;

	return vec4( H, PDF );
}

// Multiple importance sampling power heuristic of two functions with a power of two. 
// [Veach 1997, "Robust Monte Carlo Methods for Light Transport Simulation"]
float MISWeight( uint Num, float PDF, uint OtherNum, float OtherPDF ) {
	float Weight = float(Num) * PDF;
	float OtherWeight = float(OtherNum) * OtherPDF;
	return Weight * Weight / (Weight * Weight + OtherWeight * OtherWeight);
}

//==========================================================================================
// brdf
// https://github.com/EpicGames/UnrealEngine/blob/master/Engine/Shaders/BRDF.usf
//==========================================================================================

vec3 Diffuse_Lambert( vec3 DiffuseColor ) {
	return DiffuseColor * (1.0 / PI);
}

// GGX / Trowbridge-Reitz
// [Walter et al. 2007, "Microfacet models for refraction through rough surfaces"]
float D_GGX( float Roughness, float NoH ) {
	float a = Roughness * Roughness;
	float a2 = a * a;
	float d = ( NoH * a2 - NoH ) * NoH + 1.0;	// 2 mad
	return a2 / ( PI*d*d );					// 4 mul, 1 rcp
}

float Vis_Implicit() {
	return 0.25;
}

// [Kelemen 2001, "A microfacet based coupled specular-matte brdf model with importance sampling"]
float Vis_Kelemen( float VoH ) {
	// constant to prevent NaN
	return rcp( 4.0 * VoH * VoH + 1e-5);
}

// Tuned to match behavior of Vis_Smith
// [Schlick 1994, "An Inexpensive BRDF Model for Physically-Based Rendering"]
float Vis_Schlick( float Roughness, float NoV, float NoL ) {
	float k = Square( Roughness ) * 0.5;
	float Vis_SchlickV = NoV * (1.0 - k) + k;
	float Vis_SchlickL = NoL * (1.0 - k) + k;
	return 0.25 / ( Vis_SchlickV * Vis_SchlickL );
}

// Appoximation of joint Smith term for GGX
// [Heitz 2014, "Understanding the Masking-Shadowing Function in Microfacet-Based BRDFs"]
float Vis_SmithJointApprox( float Roughness, float NoV, float NoL ) {
	float a = Square( Roughness );
	float Vis_SmithV = NoL * ( NoV * ( 1.0 - a ) + a );
	float Vis_SmithL = NoV * ( NoL * ( 1.0 - a ) + a );
	// Note: will generate NaNs with Roughness = 0.  MinRoughness is used to prevent this
	return 0.5 * rcp( Vis_SmithV + Vis_SmithL );
}

vec3 F_None( vec3 SpecularColor ) {
	return SpecularColor;
}

// [Schlick 1994, "An Inexpensive BRDF Model for Physically-Based Rendering"]
vec3 F_Schlick( vec3 SpecularColor, float VoH ) {
	float Fc = Pow5( 1.0 - VoH );					// 1 sub, 3 mul
	//return Fc + (1 - Fc) * SpecularColor;		// 1 add, 3 mad

	// Anything less than 2% is physically impossible and is instead considered to be shadowing
	return saturate( 50.0 * SpecularColor.g ) * Fc + (1.0 - Fc) * SpecularColor;

}

//==========================================================================================
// shading models
// https://github.com/EpicGames/UnrealEngine/blob/master/Engine/Shaders/ShadingModels.usf
//==========================================================================================

vec3 StandardShading( vec3 DiffuseColor, vec3 SpecularColor, vec3 LobeRoughness, vec3 LobeEnergy, vec3 L, vec3 V, vec3 N ) {
	vec3 H = normalize(V + L);
	float NoL = max(0.0, dot(N, L) );
	float NoV = max(0.0, abs( dot(N, V) ) + 1e-5 );
	float NoH = max(0.0, dot(N, H) );
	float VoH = max(0.0, dot(V, H) );

	// Generalized microfacet specular
	float D = D_GGX( LobeRoughness[1], NoH ) * LobeEnergy[1];
	float Vis = Vis_SmithJointApprox( LobeRoughness[1], NoV, NoL );
	vec3 F = F_Schlick( SpecularColor, VoH );

	vec3 Diffuse = Diffuse_Lambert( DiffuseColor );
	//vec3 Diffuse = Diffuse_Burley( DiffuseColor, LobeRoughness[1], NoV, NoL, VoH );
	//vec3 Diffuse = Diffuse_OrenNayar( DiffuseColor, LobeRoughness[1], NoV, NoL, VoH );

	return Diffuse * LobeEnergy[2] + (D * Vis) * F;
}

vec3 SimpleShading( vec3 DiffuseColor, vec3 SpecularColor, float Roughness, vec3 L, vec3 V, vec3 N ) {
	vec3 H = normalize(V + L);
	float NoH = saturate( dot(N, H) );

	// Generalized microfacet specular
	float D = D_GGX( Roughness, NoH );
	float Vis = Vis_Implicit();
	vec3 F = F_None( SpecularColor );

	return Diffuse_Lambert( DiffuseColor ) + (D * Vis) * F;
}

vec3 ClearCoatShading( vec3 DiffuseColor, vec3 SpecularColor, vec3 LobeRoughness, vec3 LobeEnergy, vec3 L, vec3 V, vec3 N ) {
	const float ClearCoat			= 1.0;
	const float ClearCoatRoughness	= 0.1225;
	const float Film = 1.0 * ClearCoat;
	const float MetalSpec = 0.9;

	vec3 H = normalize(V + L);
	float NoL = saturate( dot(N, L) );
	float NoV = saturate( abs( dot(N, V) ) + 1e-5 );
	float NoH = saturate( dot(N, H) );
	float VoH = saturate( dot(V, H) );

	// Generalized microfacet specular
	float D = D_GGX( LobeRoughness[0], NoH ) * LobeEnergy[0];
	float Vis = Vis_Kelemen( VoH );

	// F_Schlick
	float F0 = 0.04;
	float Fc = Pow5( 1.0 - VoH );
	float F = Fc + (1.0 - Fc) * F0;
	F *= ClearCoat;

	float Fr1 = D * Vis * F;

	float LayerAttenuation = (1.0 - F);

	// Generalized microfacet specular
	float D2 = D_GGX( LobeRoughness[1], NoH ) * LobeEnergy[1];
	float Vis2 = Vis_Schlick( LobeRoughness[1], NoV, NoL );
	//vec3 F2 = F_Schlick( SpecularColor, VoH );
	vec3 F2 = saturate( 50.0 * SpecularColor.g ) * Fc + (1.0 - Fc) * SpecularColor;

	//vec3 Fr2 = Diffuse_Burley( DiffuseColor, LobeRoughness[1], NoV, NoL, VoH ) * LobeEnergy[2] + (D2 * Vis2) * F2;
	vec3 Fr2 = Diffuse_Lambert( DiffuseColor ) * LobeEnergy[2] + (D2 * Vis2) * F2;

	return Fr1 + Fr2 * LayerAttenuation;
}


//==========================================================================================
// image based lighting
// //https://github.com/EpicGames/UnrealEngine/blob/master/Engine/Shaders/PostProcessAmbient.usf
//==========================================================================================

// using this instead of just sampling the env map
vec3 sampleWorld(vec3 ro, vec3 rd);

vec3 DiffuseIBL(uvec2 Random, vec3 DiffuseColor, float Roughness, vec3 p, vec3 N, vec3 V) {
	vec3 DiffuseLighting = vec3(0);

	float NoV = max(0.0, dot( N, V ));

	const uint NumSamples = 16u;
	for(uint i = 0u; i < NumSamples; ++i) {
		vec2 E = Hammersley( i, NumSamples, Random );
		vec3 L = TangentToWorld( CosineSampleHemisphere( E ).xyz, N );
		vec3 H = normalize(V + L);

		float NoL = max(0.0, dot(N, L));
		float NoH = max(0.0, dot(N, H));
		float VoH = max(0.0, dot(V, H));

		if (NoL > 0.0) {
			vec3 SampleColor = sampleWorld(p, L);

			float FD90 = ( 0.5 + 2.0 * VoH * VoH ) * Roughness;
			//float FD90 = 0.5 + 2 * VoH * VoH * Roughness;
			float FdV = 1.0 + (FD90 - 1.0) * pow( 1.0 - NoV, 5.0 );
			float FdL = 1.0 + (FD90 - 1.0) * pow( 1.0 - NoL, 5.0 );

			// lambert = DiffuseColor * NoL / PI
			// pdf = NoL / PI
			DiffuseLighting += SampleColor * DiffuseColor * FdV * FdL * ( 1.0 - 0.3333 * Roughness );
			//DiffuseLighting += SampleColor * DiffuseColor;
		}
	}

	return DiffuseLighting / float(NumSamples);
}

vec3 SpecularIBL(uvec2 Random, vec3 SpecularColor, float Roughness, vec3 p, vec3 N, vec3 V) {
	vec3 SpecularLighting = vec3(0);

	const uint NumSamples = 16u;
	for(uint i = 0u; i < NumSamples; ++i) {
		vec2 E = Hammersley( i, NumSamples, Random );
		vec3 H = TangentToWorld( ImportanceSampleGGX( E, Roughness ).xyz, N );
		vec3 L = 2.0 * dot( V, H ) * H - V;

		float NoV = max(0.0, dot(N, V));
		float NoL = max(0.0, dot(N, L));
		float NoH = max(0.0, dot(N, H));
		float VoH = max(0.0, dot(V, H));

		if( NoL > 0.0 ) {
			vec3 SampleColor = sampleWorld(p, L);

			float Vis = Vis_SmithJointApprox( Roughness, NoV, NoL );
			float Fc = pow( 1.0 - VoH, 5.0 );
			vec3 F = (1.0 - Fc) * SpecularColor + Fc;

			// Incident light = SampleColor * NoL
			// Microfacet specular = D*G*F / (4*NoL*NoV) = D*Vis*F
			// pdf = D * NoH / (4 * VoH)
			SpecularLighting += SampleColor * F * ( NoL * Vis * (4.0 * VoH / NoH) );
		}
	}

	return SpecularLighting / float(NumSamples);
}

vec3 ImageBasedLightingMIS(vec3 DiffuseColor, vec3 SpecularColor, float Roughness, vec3 p, vec3 V, vec3 N, uvec2 Random) {
	vec3 Lighting = vec3(0);

	const uint NumSamples = 16u;
	for (uint Set = 0u; Set < 2u; ++Set) {
		for (uint i = 0u; i < NumSamples; ++i) {
			vec2 E = Hammersley( i, NumSamples, Random );

			vec3 L, H;
			if (Set == 0u) {
				L = TangentToWorld(CosineSampleHemisphere(E).xyz, N);
				H = normalize(V + L);
			} else if (Set == 1u) {
				H = TangentToWorld(ImportanceSampleGGX(E, Roughness).xyz, N);
				L = 2.0 * dot( V, H ) * H - V;
			}

			float NoL = max(0.0, dot(N, L));
			float NoH = max(0.0, dot(N, H));
			float VoH = max(0.0, dot(V, H));

			if (NoL > 0.0 && VoH > 0.0) {
				vec3 SampleColor = sampleWorld(p, L);

				float[] PDF = float[](
					NoL / PI,
					D_GGX( Roughness, NoH ) * NoH / (4.0 * VoH)
				);

				// MIS balance heuristic
				float InvWeight = 0.0;
				for (uint j = 0u; j < 2u; ++j) {
					InvWeight += PDF[j] * float(NumSamples);
				}
				float Weight = rcp(InvWeight);

				vec3 Shading = StandardShading(DiffuseColor, SpecularColor, vec3(Roughness), vec3(1), L, V, N);
				//vec3 Shading = SimpleShading(DiffuseColor, SpecularColor, Roughness, L, V, N);
				//vec3 Shading = ClearCoatShading(DiffuseColor, SpecularColor, vec3(Roughness), vec3(1), L, V, N);

				Lighting += SampleColor * Shading * ( NoL * Weight );
			}
		}
	}

	return Lighting;
}

//==========================================================================================
// drawing
//==========================================================================================

vec3 background(vec3 rd) {
    return texture(iChannel0, rd).rgb;
}

struct Sphere {
    vec3 position;
    float radius;
    vec3 diffuse;
    float roughness;
};
Sphere[] spheres = Sphere[](
    Sphere( vec3(0), 1.0, vec3(0.2, 0.2, 0.2), 0.0 ),
    Sphere( vec3(0), 0.5, vec3(0.2, 0.0, 0.0), 1.0 ),
    Sphere( vec3(0), 0.5, vec3(0.0, 0.2, 0.0), 0.8 ),
    Sphere( vec3(0), 0.5, vec3(0.0, 0.0, 0.2), 0.6 ),
    Sphere( vec3(0), 0.5, vec3(0.2, 0.2, 0.0), 0.4 ),
    Sphere( vec3(0), 0.5, vec3(0.0, 0.2, 0.2), 0.2 ),
    Sphere( vec3(0), 0.5, vec3(0.2, 0.0, 0.2), 0.0 )
);

bool trace(vec3 ro, vec3 rd, out float t, out vec3 p, out vec3 N, out vec3 diffuse, out vec3 specular, out float roughness) {
    t = -1.0;
    for (int i = 0; i < spheres.length(); ++i) {
        Sphere s = spheres[i];
        float d = isect_sphere(ro, rd, vec4(s.position, s.radius));
        if (d > 0.0 && (t < 0.0 || d < t)) {
            t = d;
            p = ro + rd*t;
            N = normalize(p - s.position);
            diffuse = s.diffuse;
            specular = vec3(0.04);
            roughness = max(0.04, s.roughness);
        }
    }
    return t > 0.0;
}

vec3 sampleWorld(vec3 ro, vec3 rd) {
    float t;
	vec3 p;
	vec3 N;
	vec3 diffuse;
    vec3 specular;
    float roughness;
    if (trace(ro, rd, t, p, N, diffuse, specular, roughness)) {
        return diffuse;
    }
    return background(rd);
}

bool shade(inout vec3 ro, inout vec3 rd, out vec3 color) {
    float t;
	vec3 p;
	vec3 N;
	vec3 diffuse;
    vec3 specular;
    float roughness;
	if (trace(ro, rd, t, p, N, diffuse, specular, roughness)) {
		vec3 V = -rd;
		color = ImageBasedLightingMIS(diffuse, specular, roughness, p, V, N, floatBitsToUint(random2f()));
		return true;
	}
	color = background(rd);
	return false;
}

mat3 camera(in vec3 ro, in vec3 ta, float cr) {
	vec3 cw = normalize(ta - ro);
	vec3 cp = vec3(sin(cr), cos(cr), 0.0);
	vec3 cu = normalize(cross(cw, cp));
	vec3 cv = normalize(cross(cu, cw));
	return mat3(cu, cv, -cw);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    random_init2f(fragCoord);
    float aspect = iResolution.x / iResolution.y;

    for (int i = 1; i < spheres.length(); ++i) {
        float it = iTime + float(i)/2.0;
        spheres[i].position = 2.0*vec3(sin(it), cos(it), 0.0);
    }

	vec3 total = vec3(0.0);
    const int NUM_SAMPLES = 4;
	for (int i = 0; i < NUM_SAMPLES; ++i) {
        vec2 p = snorm(fragCoord / iResolution.xy) * vec2(aspect, 1);

		vec2 off = hammersley2d(i, NUM_SAMPLES);
		//vec2 off = random2f();
		off = snorm(off) / iResolution.xy;
		p += off;

		vec3 ro = vec3(0, 0, 10);
		vec3 ta = vec3(0, 0, 0);
		mat3 cam = camera(ro, ta, 0.0);
		vec3 rd = normalize(cam * vec3(p.xy, -2.5));

		//vec3 color = render(ro, rd);
        vec3 color;
        shade(ro, rd, color);
		total += color;
	}
	total /= float(NUM_SAMPLES);

	// tonemapping
	total = total / (total + vec3(1.0));
	// gamma
	total = pow(total, vec3(1.0 / 2.2));

	fragColor = vec4(total, 1);
}
