#define Z(n) mat2(cos(n), -sin(n), sin(n), cos(n))
#define theme(x) mix(vec4(.05, .24, .3, 1.), vec4(.27, .64, .28, 1.), x)
#define r iResolution.yx
void mainImage( out vec4 f, in vec2 c )
{
	vec2 u = c.yx*2. / r;
    float j = ceil(u.x), t = 99., D, a = 5.;
    u.x = fract(u.x);    

    u = u*2.-1.;

    vec3 o = vec3(0.,0.,-4.), d = vec3(u,1.),p,s;
    for (; a <16.; a++)
    {
        s.xy = Z(a*j)*vec2(cos(u.y*3.), 
                      sin(u.y*2.+a+iTime*2.))*1.4;
        s.z = 0.25;
        D = 0.;
        for (int i = 0; i < 10; i++)
        {
            p = o + D*d;

            D += length(p.xz - s.xy) - s.z;
        }
        t = min(t,D);

    }
	t=2./(t*t);
    f = theme(smoothstep(0.9, 0., c.y/r.y))*0.2;
    f += mix(theme(c.y/r.y)*6., vec4(t),1./(1.+t));

}
