#define R iResolution.xy

void mainImage(out vec4 c, vec2 p)
{

    p = (2. * p - R) / R.y;

    float l = length(p) * 1.3;
    p /= sqrt(.6 - dot(p, p))* 0.08;


    p -= iTime + .1 * iMouse.xy;

    p.y -= cos(p.x) * .7; 


    float b = mix(smoothstep(.05, 0., mix(cos(p.x) * sin(p.y) , .2, cos(p.y) * .3)), -.5, l);

    c = .8 - vec4(-.5 / l - b, .5  *b, .1 * l, 1.); 


}
