float plot(vec2 pos) {
    float threshhold = 0.02;
    float PI = 3.14159;

    float y = (sin(pos.x*2.0*PI + iTime*2.0) / 2.0) + 0.5;

    return smoothstep(0.0, threshhold, abs(y - pos.y));
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float r = 1.0;
    float g = 1.0;
    float b = 1.0;
    float multiplier = 1.0;

    vec3 line_color = vec3(0.0, 0.0, 0.0);

	vec2 pos = fragCoord / iResolution.xy;    
    float scaled_y = pos.y;

    multiplier = plot(pos);

    vec3 fade_color = multiplier * vec3(r, scaled_y, b);    
    vec3 color = fade_color + line_color;

	fragColor = vec4(color, 1.0);
}
