highp float randFloat(vec2 uv) {
	highp float a = 12.9898;
	highp float b = 78.233;
	highp float c = 43758.5453;
	highp float dt = dot(uv.xy, vec2(a, b));
    highp float sn = mod(dt, 3.14);
    return fract(sin(sn) * c);
}

float lerpRand(vec2 uv) {
    float res = 256.0;
    uv += 0.5 / res;
    vec2 a = floor(uv * res);
    vec2 b = a + 1.0;
    a /= res;
    b /= res;

    float tl = randFloat(a);
    float tr = randFloat(vec2(b.x, a.y));
    float bl = randFloat(vec2(a.x, b.y));
    float br = randFloat(b);

    //tl = textureLod(iChannel0, a, 0.0).r;
    //tr = textureLod(iChannel0, vec2(b.x, a.y), 0.0).r;
    //bl = textureLod(iChannel0, vec2(a.x, b.y), 0.0).r;
    //br = textureLod(iChannel0, b, 0.0).r;

    float u = (uv.x - a.x) / (b.x - a.x);
    float v = (uv.y - a.y) / (b.y - a.y);
    float top = mix(tl, tr, u);
    float bot = mix(bl, br, u);
    //return top;
    return mix(top, bot, v);
}

vec2 rand1(in vec2 uv) {
    return textureLod(iChannel0, uv, 0.0).gr;
}

vec2 rand2(in vec2 uv) {
    return vec2(lerpRand(uv), lerpRand(uv + vec2(1.0, 0.0)));
}

