{	vec2 uv = fragCoord.xy / iResolution.xy;
	float t = iTime * 0.001;
    //randRGBA(fragColor, uv * 1.0);
 	vec2 uv2 = (uv + 0.5) / 64.0;

 	//vec2 rg = textureLod(iChannel0, uv2, 0.0).yx;
 	//vec2 rg = vec2(lerpRand(uv2 * 0.5), lerpRand(uv2 * 0.5 + vec2(1.0, 0.0)));

 	uv2 *= 2.0;

 	vec2 rg1 = rand1(uv2);
 	vec2 rg2 = rand2(uv2);
 	vec2 rg = rg1;

 	vec4 m = iMouse / iResolution.xxxx;

 	if(m.z > 0.0) {
        rg = rg2;
 	}

 	vec4 col = vec4(rg.rg, 0.0, 1.0);


 	fragColor = col;
}
