// original: https://www.shadertoy.com/view/ltBGzt

vec4 rgb(int r, int g, int b)
{
    return vec4(float(r)/255., float(g)/255., float(b)/255., 1);
}

vec4 circle(vec2 uv, vec2 origin, float radius, vec4 color)
{
    float d = length(uv - origin) - radius;// distance from uv to the edge of the circle.

//    float a = 1.0 - step(0., d);
//    float delta = 0.006;
    float delta = fwidth(d);
    float a = 1.0 - smoothstep(-delta, delta, d);

    return vec4(color.rgb, color.a * a);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.yy;
    vec2 center = vec2(iResolution.x/iResolution.y*0.5, 0.5);

	fragColor = rgb(251,199,38);

    vec4 c2 = circle(uv, center + 0.3*vec2(cos(iTime), sin(iTime)), 0.1, rgb(3,72,51));
	fragColor = mix(fragColor, c2, c2.a);

    vec4 c1 = circle(uv, center, 0.3, rgb(204,1,46));
	fragColor = mix(fragColor, c1, c1.a);
}
