vec2 Turbulance(inout vec2 uv, float Freq,float Amp,float iterations)
{
    for(float i = 0.;i < iterations;i++)
    {        
    	uv.y +=  sin(uv.x * Freq + iTime) * Amp;
    	uv.x +=  cos(uv.y * Freq + iTime) * Amp;        
        Amp*=0.5;
        Freq *= 2.;
    }
    return uv;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;        

    uv *= 1.;    
    Turbulance(uv,12.,.07,5.);


    vec4 t = texture(iChannel0,uv);

    fragColor = t;
}
