// "Checkerboard Tunnel"
// by Ulf Biallas | https://twitter.com/ulfbiallas

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {

    vec2 mouse = iMouse.xy;
    float dx = mouse.x - fragCoord.x;
    float dy = mouse.y - fragCoord.y;

    float dist = pow(pow(dx, 2.0) + pow(dy, 2.0), 1.0 / 2.0);
    dist = 500.0 * pow(dist, 0.1);

	float blockSize = 35.0;

    dist = mod(dist, blockSize) - 0.5 * blockSize;
    vec4 color;
    bool distFlag = false;
    if (dist < 0.0) {
        distFlag = true;
    }

    float angle = atan(dy / dx) + 0.5 * 3.14;
    float segmentSize = 3.14 / 4.0;

    angle = mod(iTime+angle, segmentSize) - 0.5 * segmentSize;
    bool angleFlag = false;
    if (angle <= 0.0) {
        angleFlag = true;
    }

    color = vec4(1.0, 1.0, 1.0, 1.0);
    if( (angleFlag && distFlag)) {
        color = vec4(0.0, 0.0, 0.0, 1.0);
    }
    if( (!angleFlag && !distFlag)) {
        color = vec4(0.0, 0.0, 0.0, 1.0);
    }

    fragColor = color;
}
