vec4 sunColor(vec2 uv, vec2 mouse)
{
    float radius = 0.1;//*abs(sin(0.2*iTime));
    float dist = (distance(mouse, uv)/radius);
    float d = 1.0/pow(dist, 1.0);
	return vec4(d, d, 0.0, 1.0);    
}

vec2 transform(vec2 a){
    return (a.xy-.5*iResolution.xy)/iResolution.y;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = transform(fragCoord); vec2 mouse = transform(iMouse.xy);
    fragColor = sunColor(uv, mouse);

}
