// inspired from https://www.youtube.com/watch?v=Jri0del_6t4


// Gabor kernel
#define G(U,a,v) exp(-.5*dot(U,U)*1e3) * sin( 100.*dot(U,sin(a+vec2(1.57,0)))-20.*(v)*sin(.5*iDate.w) ) 


void mainImage( out vec4 O, vec2 U )
{
	U = (U+U - (O.xy=iResolution.xy) ) / O.y;
    O -= O;

    for (float i=-3.; i<4.; i++)    // 4 lines of 7 splats
        O +=   G( U-vec2(i/5.,  .7), 1.57, i)
             + G( U-vec2(i/5., -.7), 1.57, i)
             + G( U-vec2(-.7, i/5.), 0.  ,-i)
             + G( U-vec2( .7, i/5.), 0.  ,-i);

	O = .5 + .5*O; 
}
