vec3 fillColor (vec3 color, float start, float blur, float t) {
	float step1 = smoothstep(start, start-blur, t);
    step1 *= step(start - 0.1, t);

    vec3 color1 = color * step1;

    return color1;
}

float fillColor2 (float start, float t) {
    float step1 = step(start, t) * (1.0 - step(start + 0.1, t));

    return step1;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - 0.5 * iResolution.xy)/iResolution.xy;

    // uv -= 0.5;
    float blur = 1.5;

    float midwidth = 0.03;
    float midpos = 0.3;

    float skyWidth = midwidth * 7.0;

    float x = uv.x;

    float y = uv.y;

    y += x * x;

    // case3

    vec3 color1 = smoothstep(fwidth(y * blur), 0.0, abs(y - midpos + midwidth * 1.0) - midwidth) * vec3(0.835, 0.176, 0.153);
    vec3 color2 = smoothstep(fwidth(y * blur), 0.0, abs(y - midpos + midwidth * 3.0) - midwidth) * vec3(0.902, 0.580, 0.212);
    vec3 color3 = smoothstep(fwidth(y * blur), 0.0, abs(y - midpos + midwidth * 5.0) - midwidth) * vec3(0.980, 0.894, 0.298);
    vec3 color4 = smoothstep(fwidth(y * blur), 0.0, abs(y - midpos + midwidth * 7.0) - midwidth) * vec3(0.294, 0.635,0.294);
    vec3 color5 = smoothstep(fwidth(y * blur), 0.0, abs(y - midpos + midwidth * 9.0) - midwidth) * vec3(0.235, 0.553, 0.824);
    vec3 color6 = smoothstep(fwidth(y * blur), 0.0, abs(y - midpos + midwidth * 11.0) - midwidth) * vec3(0.114, 0.141, 0.498);
    vec3 color7 = smoothstep(fwidth(y * blur), 0.0, abs(y - midpos + midwidth * 13.0) - midwidth) * vec3(0.408, 0.192, 0.545);

	vec3 mixSkyColor = mix(vec3(0.505, 0.620, 1.000), vec3(1.000, 0.768, 0.624), y);
    vec3 skyColor = smoothstep(0.0, fwidth(y * blur), abs(y - midpos + skyWidth) - skyWidth) * mixSkyColor;
    vec3 rainbowColor = color1 + color2 + color3 + color4 + color5 + color6 + color7;

    vec3 color = rainbowColor + skyColor;

    // case2
    /*
    vec3 color1 = vec3(0.835, 0.176, 0.153) * fillColor2(0.4,y);
    vec3 color2 = vec3(0.902, 0.580, 0.212) * fillColor2(0.3, y);
    vec3 color3 = vec3(0.980, 0.894, 0.298) * fillColor2(0.2, y);
    vec3 color4 = vec3(0.294, 0.635, 0.294) * fillColor2(0.1, y);
    vec3 color5 = vec3(0.235, 0.553, 0.824) * fillColor2(0.0, y);
    vec3 color6 = vec3(0.114, 0.141, 0.498) * fillColor2(-0.1, y);
    vec3 color7 = vec3(0.408, 0.192, 0.545) * fillColor2(-0.2, y);

    vec3 rainbowColor = color1 + color2 + color3 + color4 + color5 + color6 + color7;

	vec3 color = rainbowColor;
    */

    // case1
    /*
    vec3 color1 = fillColor(vec3(0.835, 0.176, 0.153), 0.5, 0.00001, y);
    vec3 color2 = fillColor(vec3(0.902, 0.580, 0.212), 0.4, 0.00001, y);
    vec3 color3 = fillColor(vec3(0.980, 0.894, 0.298), 0.3, 0.00001, y);
    vec3 color4 = fillColor(vec3(0.294, 0.635,0.294), 0.2, 0.00001, y);
    vec3 color5 = fillColor(vec3(0.235, 0.553, 0.824), 0.1, 0.00001, y);
    vec3 color6 = fillColor(vec3(0.114, 0.141, 0.498), -0.0, 0.00001, y);
    vec3 color7 = fillColor(vec3(0.408, 0.192, 0.545), -0.1, 0.00001, y);

	vec3 rainbowColor = color1 + color2 + color3 + color4 + color5 + color6 + color7;

	vec3 color = rainbowColor;
	*/

    fragColor = vec4(color,1.0);
}
