void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    float pi = 3.14;
    vec2 co = fragCoord/iResolution.xy;
    co = (co - vec2(0.5, 0.5));
    vec2 uv = fragCoord / iResolution.xy;

    float ampScale = 0.7;
     float amp;

    // texture( <name>, coordinate) => vec4
    amp = texture ( iChannel0, vec2(co.x / ampScale + 0.5, 0.0)).x;






    vec2 c = vec2(0.5, 0.5);
    vec2 d = uv - c;
    float length =  length(d);

    float lowFreq = texture(iChannel0, vec2(0.1,0.)).x;

    float thickness = pow(amp, 5.) * 0.3;
    float radiuscentre = 0.3 + lowFreq * 0.1;
    float radius1 = radiuscentre - (thickness/2.) ;// * (1.0-amp/4.0);
    float radius2 = radiuscentre + (thickness/2.);



    float x;
    float y;
    float z;



    if (length > radius1) {
        x = amp ;
        y = amp ;
        z = amp * 0.6;
    } else {
        x = y = z = 0.0 ;
        //x = pow(length/radius1, 6.) * amp;
        //y = pow(length/radius1, 6.) * amp;
        //z = pow(length/radius1, 6.) * amp *0.6;
    }
    if ( length > radius2) {
        x = 0.0;
        y = 0.0;
        z = 0.0;
    }   

    fragColor = vec4(
        x*uv.x,
        y * uv.y,
        z ,
        1.0
    ); 
}
