#define _HalfMaxTime (2. * .5)

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord / iResolution.xy;
    float effect = texture(iChannel0, uv).r;
    vec4 tex = texture(iChannel1, uv);

    float v = (cos(iTime / _HalfMaxTime) * .5 + .5); // Get current time
    v *= max(effect, float(effect < v));

    // Output to screen
    fragColor = /*vec4(vec3(effect), 1.); //*/tex * vec4(vec3(v), 1.);
}

#define pi 3.14

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord / iResolution.xy;
    float dir = atan((uv.y - .5) / (uv.x - .5)) * 180. / pi; // Get direction of pixels
    // And then convert it to degrees

    dir /= 180.; // Normalize
    dir = fract(dir); // Take only fraction of it

    fragColor = vec4(dir, 0., 0., 1.); // And output
}
