void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float t = iTime/10.;
    float color = 0.;
    fragCoord-= iResolution.xy/2.;
    float w = iResolution.x/2.;
    float h = iResolution.y/2.;

    //wormy
    for(float i = 0.; i < 12.; i+=.25){
    	float x = (w/2.)*sin(i/30.+t*4.);
    	float y = (h/10.)*cos(i/10.+t*8.) + 2.*sin((x*3.+t*200.)/25.);
        color += .2/distance(fragCoord, vec2(x, y));
    }

    //stars
    for(float i = 0.; i < 25.; i++){
        float x = mod(i+(sin(i))*(t+500.)*500., w*2.+200.)-w-100.;
        float y = h*sin(cos(i*524.)*i*5.+i*2.);
        color += (.2*sin(i)+.2)/distance(fragCoord, vec2(x,y));
    }

    fragColor = vec4(color);

    //for more fun:
    //fragColor = vec4(abs(sin(color*34.)),abs(sin(color*45.+3.)),abs(cos(color*23.+23.)), 255);
}
