vec2 skew (vec2 st) {
    vec2 r = vec2(0.0);
    r.x = 1.5*st.x;
    r.y = st.y+0.5*r.x;
    return r;
}

void mainImage( out vec4 o, in vec2 f )
{
    float	t = iTime;
	vec2 R = iResolution.xy,
          uv  = vec2(f-R/2.) / R.xy;

    mat2	rot = mat2(
    cos(t),
        sin(t),
        -sin(t),
        cos(t)

    );

	vec4	teal = vec4(0.3,.7,.9,1.0);
    vec4	orange = vec4(0.95,.5,.1,1.0);
    vec2	old;
    old = uv*3.;
    uv*=10.;
    old *= rot;

    uv = skew(uv);
	uv = fract(uv);

    float	r1 = 1.*.125*sin(t+0.+(old.x+old.y) );
    float	r2 = 1.*.125*sin(t+1.57+(old.x+old.y) );
    o =  teal*(step(uv.y, uv.x) * step(r2, uv.y) );
    o +=  orange*(step(uv.x, uv.y) * step(r1, uv.x) );


}
