// Based on https://www.shadertoy.com/view/lsfyDn

#define TWO_PI 6.28318530718

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    float ar = iResolution.x/iResolution.y;
    uv.x = uv.x*ar;
    vec2 m = iMouse.xy/iResolution.xy;
    float t = iTime;

    float speed = 10.0;
    float amp = .5;
    float sep = 1.5;

    float comb = 1.;

    for (float i = 0.; i < 7.; i++) {
        comb *= distance(uv,vec2(sin(t+i*TWO_PI/7.)/(3.*(sin(t)/5.+1.))+.9,cos(t+i*TWO_PI/7.)/(3.*(sin(t)/5.+1.))+.5))*3.0;
    }

    float red = sin(comb*(3.0)-t*speed)/2.0+0.5;
    float green = sin(comb*(3.0+sep)-t*speed)/2.0+0.5;
    float blue = sin(comb*(3.0+sep*2.0)-t*speed)/2.0+0.5;

    vec3 color = vec3(red-comb+amp,green-comb+amp,blue-comb+amp);

	fragColor = vec4(color,1.0);
}
