#define PI 3.1415926535897932384626433832795

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy * 2. - 1.;
	uv.y *= iResolution.y / iResolution.x;

	vec2 center = vec2(0.);

	uv.y = abs(uv.y);

	// Red part
	float angle = atan(uv.y - center.y, uv.x - center.x)
					/ (PI * 2.) + .5;

	float goodAngle = angle / 3.;

	float dist = texture(iChannel0, vec2(goodAngle, 0.)).x;

	float red = 1. - distance(uv, center) / dist;

	// Green part
	goodAngle = angle / 3. + 1. / 3.;

	dist = texture(iChannel0, vec2(goodAngle, 0.)).x;

	float green = 1. - distance(uv, center) / dist;

	// Blue part
	goodAngle = angle / 3. + 2. / 3.;

	dist = texture(iChannel0, vec2(goodAngle, 0.)).x;

	float blue = 1. - distance(uv, center) / dist;

	fragColor = vec4(red, green, blue, .5) * 2.;
}
