// vincent francois - cyanux/2014

float T;

float noise(vec2 p, float m)
{
	return fract(mod(m, pow(p.x+p.y, 0.5)));
}

float render(vec2 p)
{
	return noise(p - 0.5, 3.0 + 0.4 * T);
}

#define D (0.005)

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    T = sin(iTime);
	vec2 p = fragCoord.xy / iResolution.xy;

	float m = (
		render(p) +
		render(p + vec2( D,    0.0)) +
		render(p + vec2(-D,    0.0)) +
		render(p + vec2( 0.0,  D  )) +
		render(p + vec2( 0.0, -D  ))
		) * 0.2;

	fragColor = vec4(0.0, m, 0.0, 0.0);
}
