float alterDistance(vec2 v1, vec2 v2, float strength, float speed) 
{
  return sin(dot(normalize(v1), normalize(v2)) * strength + speed * iTime) / 100.0;
}

float alterDistance(vec2 uv, vec2 center) 
{
  vec2 diff = center - uv;
  float len = length(diff);

  len += alterDistance(diff, vec2(0.0, 1.0), 10.0, 2.0);
  len -= alterDistance(diff, vec2(1.0, 0.0), 10.0, 2.0);

  return len;
}

float limit(float x, float min, float max)
{
    if (x < min)
        x = 0.0;

    if (x > max)
        x = 1.0;

    return x;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

	vec4 color = texture(iChannel0, uv);

	vec2 center = vec2(0.5, .5); 

    vec2 p = ceil(uv * 300.0) / 300.0;

	float d = alterDistance(center, p);

    vec4 pink = vec4(0.89, 0.0, 0.69, 1.0) * 0.75;

    float t = iTime * 0.1;

    float cloud = texture(iChannel1, p +  vec2(t, t)).r;
    float cloud2 = texture(iChannel1, 0.5 * p + vec2(t, -t)).r;

    cloud = mix(cloud, cloud2, 0.5);

    float r1 = 0.2;
    float r2 = 0.225;
    float r3 = 0.25;

    if (d <= r1)
    {
        fragColor = color;
    }
    else if (d <= r2)
    {
        fragColor = pink;
    }
    else if (d <= r3)
    {
        float nd = (d - r2) / (r3 - r2); 
    	fragColor = limit(cloud, 0.3, mix(0.3, 0.4, nd)) * pink;
    }
    else
    {
    	fragColor = limit(cloud, 0.3, 0.4) * pink;
    }
}
