void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float pi = 3.1415926535897;
    vec2 uv = fragCoord.xy / iResolution.xy;
    vec2 z, c;
    int maxIterations = 256;
    c = uv;
    z = c;
    vec4 color;
    float cx = uv.x+0.5*sin(iTime/5.0);
    float cy = uv.y+0.5*cos(iTime/3.0);

    float v = sin(sqrt(100.0*(cx*cx+cy*cy)));
    v += sin(uv.x*10.0+iTime);
    v += cos(uv.y*4.0+iTime);


    color = vec4(sin(v*pi), cos(v*pi), sin(v*pi + 0.5*pi*v), 1.0);
    vec2 xy = fragCoord.xy / iResolution.xy;//Condensing this into one line
    vec4 texColor = texture(iChannel0,xy);//Get the pixel at xy from iChannel0
    vec4 texColorB = texture(iChannel1,xy);

    if (texColor.g < 0.2 && texColor.r < 0.25 && texColor.b < 0.25) //if green screen
    {
        fragColor = color;
    }
    else
    {

        color = texColor;

        if (color.r < 0.3 && color.b < 0.1 && color.g < 0.5)
        {
             v += sin(uv.x*10.0+iTime);
             v += cos(uv.y*4.0+iTime);
             color = vec4(sin(v*pi), cos(v*pi), sin(v*pi + 0.5*pi*v), 1.0);
        }
    	fragColor = vec4(mix(color.rgb, texColor.rgb, .5), 1);//Set the screen pixel to that c
    }
}
