void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

    float t = iTime;
    float bpm = 1.0 / 60.0 * 126.0;
    float pi = 3.14159265359;

    vec3 col0 = vec3(0.459, 0.306, 0.247);
    vec3 col1 = vec3(0.404, 0.455, 0.337);
    vec3 col2 = vec3(0.925, 0.792, 0.694);
    vec3 col3 = vec3(0.314, 0.455, 0.573);
    vec3 col4 = vec3(0.11, 0.133, 0.259);
    vec3 col5 = vec3(0.729, 0.263, 0.278);
    vec3 col6 = vec3(0.0, 0.0, 0.0);

    vec2 nuv = uv * 2.0 - 1.0;

    float t0 = texture(iChannel0, vec2(0.0, 0.25)).x;
    float t1 = texture(iChannel0, vec2(0.4, 0.25)).x;

    t0 = max(0.0, t0 - 0.66);
    t0 = pow(t0 * 12.0 * abs(t * bpm * pi), 3.0);
    t0 = min(1.0, t0);

    float f0 = abs(sin(uv.x * 20.0 + t * bpm * pi));
    float f1 = abs(cos(uv.x * 20.0));

    f0 = smoothstep(0.0, 1.0 - 0.5 * t1, f0);
    f1 = smoothstep(0.0, 1.0, f1);

    vec3 c = vec3(0.0);

    c += mix(f0 * col0, f1 * col1, 0.0);
    c += mix(f0 * col0, f1 * col1, 1.0);

    c = mix(c, col2, pow(abs(nuv.y), 3.0));

    float fwave0 = sin(uv.x * pi * 12.0) * 0.03 + cos(uv.x * pi * 21.0 + t * bpm * 12.0) * 0.02;
    float fwavedel0 = sin(uv.x * pi * 9.0 + t * bpm * 8.0);
    float ftree0 = smoothstep(0.95, 1.0, 1.0 - abs(nuv.y + fwave0 - 0.1 + cos(t * bpm * 0.5) * 0.4));

    c = mix(c, mix(col2, c, fwavedel0), ftree0);

    float fwave1 = sin(uv.x * pi * 12.0) * 0.03 + cos(uv.x * pi * 21.0 + t * bpm * 12.0) * 0.02;
    float fwavedel1 = sin(uv.x * pi * 9.0 + t * bpm * 8.0);
    float ftree1 = smoothstep(0.95, 1.0, 1.0 - abs(nuv.y + fwave1 - 0.1 + sin(t * bpm * 0.5) * 0.4));

    c = mix(c, mix(col2, c, fwavedel1), ftree1 - t0 * 0.1);    

    float fwave2 = sin(uv.x * pi * 3.0) * 0.12 + cos(uv.x * pi * 17.0 + t * bpm * 9.0) * 0.02;
    float fwavedel2 = sin(uv.x * pi * 9.0 + t * bpm * 8.0);
    float ftree2 = smoothstep(0.92 + t1 * 0.05, 1.0, 1.0 - abs(nuv.y + fwave2 + 0.3));

    c = mix(c, mix(col3, c, fwavedel2), ftree2);

    float fwave3 = sin(uv.x * pi * 7.0) * 0.03 + cos(uv.x * pi * 23.0 + t * bpm * 11.0) * 0.02;
    float fwavedel3 = sin(uv.x * pi * 9.0 + t * bpm * 8.0);
    float ftree3 = smoothstep(0.92 + t1 * 0.04, 1.0, 1.0 - abs(nuv.y + fwave3 + 0.2 + sin(t * bpm * 0.1) * 0.2));

    c = mix(c, mix(col3, c, fwavedel3), ftree3);

    float fblinds = step(min(0.9, smoothstep(0.0, 1.0, t * 0.20 - 0.75)), abs(nuv.y));

    c = mix(c, col6, fblinds);

    fragColor.xyz = c;
}
