float t;

const vec3 a = vec3(61.0, 44.0, 32.0);
const vec3 b = vec3(79.0, 54.0, 33.0);
const vec3 c = vec3(155.0, 121.0, 68.0);
const vec3 d = vec3(208.0, 174.0, 111.0);
const vec3 e = vec3(250.0, 244.0, 205.0);

void mainImage(out vec4 fragColor, in vec2 fragCoord) {

    vec2 uv = fragCoord.xy / iResolution.xy;

    vec3 image = texture(iChannel0, uv).xyz;

    float reduction = 1.4;
    float amount = 5.0;
    float radius = length(uv - 0.5) * reduction;

    float magnitude = (1.0 - radius) * (amount - 1.0);

    float frame = float(iFrame);
    float flicker = sin(frame * sin(frame * 0.1));
    vec3 layer = a.xyz / 255.0;

    t = magnitude;// + flicker;
	layer = mix(layer, b.xyz / 255.0, clamp(t, 0.0, 1.0));

    t = magnitude - 1.0;
    layer = mix(layer, c.xyz / 255.0, clamp(t, 0.0, 1.0));

    t = magnitude - 2.0;
    layer = mix(layer, d.xyz / 255.0, clamp(t, 0.0, 1.0));

    t = magnitude - 3.0;
    layer = mix(layer, e.xyz / 255.0, clamp(t, 0.0, 1.0));

    float translucency = mix( 0.9, radius, iMouse.x / iResolution.x);
    fragColor = vec4(mix(image, layer, translucency), 1.0);

}
