void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 position = ( fragCoord.xy / iResolution.xy );

	position.x = position.x * 1.7;

	vec2 mouse = iMouse.xy / iResolution.xy;

	mouse.x = mouse.x * 1.7;

	float radius = 1.25*iTime;

	float posDist = distance(position, mouse);

	float dist2 = 5.0*abs(radius - posDist);

	float distort = 1.0-(sin(dist2)+1.0)/2.0;

    vec2 samplePos = fragCoord/iResolution.y;

    samplePos= samplePos * distort;

    vec4 color = texture(iChannel0, samplePos);

    vec4 tint = vec4(0.5, 0.8, 0.9, 1);

    tint = tint * (1.0 - distort);

    color = color + tint/2.0;

	fragColor = color;
}
