void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 position = fragCoord.xy / iResolution.xy;
    vec4 gs = vec4(0.,1.,0.,0.);
    vec4 col;
    vec3 diff;
    vec4 vid;
    if ( fragCoord.y > iResolution.y / 2.0 ) {
        col = texture(iChannel1, position);
        diff = col.xyz - gs.xyz;
        if(dot(diff, diff) < 0.50) {
            col = texture(iChannel0, position);
        }
    	vid = col;
    }
    else {
    	position.y = 1.0 - position.y * 2.0;
        col = texture(iChannel1, position);
        diff = col.xyz - gs.xyz;
        if(dot(diff, diff) < 0.50) {
            col = texture(iChannel0, position);
        }
        vid = col*0.7;
    }
    fragColor = vid;
}
