float mic(float x, float y){
 return texture(iChannel1, vec2(abs(mod(x+1.,2.)-1.),y)).r*.1;   
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

	vec2 uv =  fragCoord.xy / iResolution.xy ;
    uv.x = uv.x;
    float t = iTime;

    uv-=.5;
    vec2 p = uv;
    p.x = abs(p.x);
    p+=.5;
    float a = atan(uv.x,uv.y);
    float d = length(uv);
    a = abs(a);
    a+=sin(d*5.+t+mic(.2,1.)*10.)*.02;
    float amp = .02;
    amp*=(1.0-d);
    d-=abs(uv.y)*d*(sin(a*2.+mic(a/3.14,.1)*20.+t+sin(t))*amp+amp+mic(a/3.14,.1)*.1);

    uv.x = sin(a);
    uv.y = cos(a);
    uv*=d;

    uv+=.5;
    uv.xy-=mic(uv.x, 1.0-uv.y)*.005;

    vec4 c = texture(iChannel0, p);
    vec4 o = texture(iChannel2, uv);
    o-=d*pow(mic((a/1.5707),0.25),2.)*1.;
   c.rgb+=mic(o.r, 0.)*(sin(vec3(1.+sin(iTime*.13), 2.+cos(iTime*.13), 4.)+iTime)*.5+.5)*20.;
    o = mod(o,1.);
    //c = mod(c,1.);
    c = mix(c,o,.9);


	fragColor = c;
}

