void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    vec2 o = vec2(0., 1.1/iResolution.y);
    float m = mix(
        texture(iChannel0, uv-o).x,
        texture(iChannel1, vec2(uv.x, .33)).x,
       	max(ceil(1.-fragCoord.y), 0.));
    //m*=(1.0-pow(1.0-uv.x, 5.))*.3+.75;
    //m*=(1.+iMouse.x/iResolution.x);
    fragColor = vec4(m,m,m,1.);
}
