void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    //float t = ;
    vec2 mouse = iMouse.xy / iResolution.xy;
    float factor = 100.0 * mouse.x;

    fragCoord.x = fragCoord.x - (mod(fragCoord.x, factor*.5));
    fragCoord.y = fragCoord.y - (mod(fragCoord.y, factor*.5));
	vec2 px = fragCoord.xy / iResolution.xy;

    fragColor = texture(iChannel0, vec2(px.x, px.y));
}
