void mainImage(out vec4 r,in vec2 v)
{
	vec2 u=v/iResolution.xy,h=u-.5;
    h.x*=1.8;
    float t=iTime,s=clamp(sin(t),.1,.9),c=sin(u.x*99.0);
    c+=mix(cos(u.y*50.0+t*.2),c,s);
    c*=mix(cos(c+dot(h,h)*.7+t*.2),c,clamp(cos(t*.8),.1,.5));
	c*=mix(sin(dot(h,h)),c,s*s);
	c=pow(c,.8);
    r=vec4(c,c,c,c);
}
