const float size=256.;

vec4 copper_bar(vec2 pos) {
   	return vec4(cos(pos.y * size), cos(pos.y * size), cos(pos.y * size),1);
}


vec2 rotate(vec2 pos, float angle) {
    float c = cos(angle);
    float s = sin(angle);
    return mat2(c, s, -s, c) * pos;
}


vec2 translate(vec2 pos) {
    return pos + vec2(0.1, 0.0);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy/ iResolution.xy;
    vec2 pos=uv*2.0;
    float t=iTime;

    uv.y +=uv.y+(t/50.);

    vec2 cop_pos = pos;

    vec4 b = copper_bar(cop_pos);
    vec3 tc=texture(iChannel0,uv).xyz;
    vec3 tc2=texture(iChannel0,uv-vec2(0.0,-0.02)).xyz;

    fragColor = vec4(b*1.2);

    fragColor *= vec4(tc*tc2,1.0);

}
