#define ampl 1.
#define seuil .0
#define steps 100.
#define space .1
#define def .2
#define opacity .8

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

    // float sound = texture(iChannel0,vec2(log2(1.+floor(steps*uv.x)/steps),.25)).r;
    // float sound = texture(iChannel0,vec2((floor(steps*uv.x)/steps),.25)).r;
    float sound = texture(iChannel0,vec2(exp2(floor(steps*uv.x)/steps)-1.,.25)).r;

    sound *=ampl;
    sound -=seuil;
    sound = max(def, sound);
    if (uv.x*steps-floor(uv.x*steps)<space)sound = 0.;

    vec4 color = texture(iChannel1,uv);
       // uv.y +=texture(iChannel0,vec2(floor(steps*uv.x)/steps,1)).r/20.; // make the spectrum analysis dance with the waveform â™«â™¥
    if (abs((0.,5.*uv.y-1.5))< sound*sound*sound)color =mix(color, vec4(1),opacity);
	fragColor =color;
}
