void mainImage( out vec4 f, in vec2 p )
{
    float n = 0.;
    vec2 c = vec2(-.745,.186) + 3. * (p.xy/iResolution.y-.5)*pow(.01,1.+cos(.4*iTime) * 1.3 * sin(iTime)), z=c*n;

    for( int i=0; i<512; i++ )
    {
        z = vec2( z.x*z.x - z.y*z.y, 2.*z.x*z.y ) + c;

        if( dot(z,z)>1e10 ) break;

        n++;
    }

    f = .5 + .5*cos( vec4(30,40,110,0) + 1.05*(n - log2(log2(dot(z,z)))) );
}
