void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec3 fColor = vec3(.0);
	vec2 uv = fragCoord.xy / iResolution.xy;
    uv = 2.0 * uv - 1.0;
    uv.x *= iResolution.x / iResolution.y;


   	vec2 oPos00 = vec2(.0);
   	vec2 oPos01 = vec2(.0,.2);
   	vec2 oPos10 = vec2(.0,-.3);
   	vec2 oPos11 = vec2(.3,.0);

    float count = 15.;

    for(float i = 0.; i <  15.; ++i)
    {
       vec2 oPos00 = vec2(cos(iTime*0.5+i/ count * 6.28+0.5)*0.5,sin(i/count * 6.28)*0.5);
       vec2 oPos01 = vec2(cos(iTime*0.5+i/ count * 6.28+0.48)*0.5,sin(i/count * 6.28)*0.5);
       vec2 oPos10 = vec2(cos(iTime*0.5+i/ count * 6.28+0.45)*0.5,sin(i/count * 6.28)*0.5);

       float o00 = length(oPos00 - uv) - 0.05; 
       float o01 = length(oPos01 - uv) - 0.05; 
       float o10 = length(oPos10 - uv) - 0.05; 

       vec3  mat00 = (1. - smoothstep(0.0,5.0/iResolution.x,o00)) * (vec3(1.0,0.0,0.0)*1.0);
       vec3  mat01 = (1. - smoothstep(0.0,5.0/iResolution.x,o01)) * (vec3(0.0,1.0,0.0)*1.0);
       vec3  mat10 = (1. - smoothstep(0.0,5.0/iResolution.x,o10)) * (vec3(0.0,0.0,1.0)*1.0);


       fColor += mat00;
       fColor += mat01;
       fColor += mat10;  
    }

	fragColor = vec4(fColor,1.0);
}
