#define PI 3.141592

// Number of lines
#define N 10.0

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 res = iResolution.xy;
	fragCoord /= res.x;
    fragCoord.y += 0.2;

    // in [-1..1]
    fragCoord = (fragCoord - 0.5) * 2.0;
    float alpha = 1.0 - length(fragCoord);

    vec4 col = vec4(0.2, 0.1, 1.0, 1.0);


    vec2 coord = fragCoord * (N - 1.0) * 2.0;
    float dist = length(coord);

    // the grid in itself
    float func = cos(PI * dist - iTime * 2.0) * 0.2;
	fragColor = col + (func * alpha);

}
