//These are the locations of all the objects in the shader
vec2 HeadLoc = vec2(0.5, 0.75);
vec2 BodyLoc = vec2(0.5, 0.27);
vec2 RightArmLoc = vec2(0.44, 0.302);
vec2 LeftArmLoc = vec2(0.56, 0.302);
vec2 RightLegLoc = vec2(0.47, 0.122);
vec2 LeftLegLoc = vec2(0.53, 0.122);
vec2 PantsMiddleLoc = vec2(0.5, 0.182);
vec2 RightShoeLoc = vec2(0.47,0.05);
vec2 LeftShoeLoc = vec2(0.53,0.05);
vec2 GroundLoc = vec2(0.5, 0.1);
vec2 HouseLoc = vec2(0.15, 0.25);
vec2 DoorLoc = vec2(0.15, 0.22);
vec2 Window1Loc = vec2(0.125, 0.27);
vec2 Window2Loc = vec2(0.175, 0.27);

//These are all the float values
float Radius = 0.062;
float BodyWidth = 0.05;
float BodyHeight = 0.087;
float ArmWidth = 0.01;
float ArmHeight = 0.055;
float LegWidth = 0.02;
float LegHeight = 0.07;
float PantsWidth = 0.01;
float PantsHeight = 0.01;
float ShoeWidth = 0.02;
float ShoeHeight = 0.02;
float GroundLength = 0.5;
float GroundWidth = 0.1;
float HouseLength = 0.05;
float HouseHeight = 0.05;
float DoorLength = 0.01;
float DoorHeight = 0.02;
float WindowLength = 0.015;
float WindowHeight = 0.015;

//These are all the color values
vec4 SkyColor = vec4(0.16,0.8,0.98,1.0);
vec4 NightColor = vec4(0.0,0.14,0.54,1.0);
vec4 GroundColor = vec4(0.0,0.5,0.0,1.0);
vec4 SunColor = vec4(1.0,0.85,0.14,1.0);
vec4 BodyColor = vec4(0.77,0.64,0.45,1.0);
vec4 ShirtColor = vec4(1.0,1.0,0.0,1.0);
vec4 PantsColor = vec4(0.0,0.0,1.0,1.0);
vec4 ShoeColor = vec4(0.0,0.0,0.0,1.0);
vec4 HouseColor = vec4(0.3,0.07,0.07,1.0);
vec4 DoorColor = vec4(0.85,0.79,0.79,1.0);

//float freq = 3.0;
//float amp = 0.1;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    float aspectRatio=iResolution.y/iResolution.x;
    uv.y*=aspectRatio;
    HeadLoc.y*=aspectRatio;

    //These all make the sun gradient
    float d = distance(iResolution.xy*0.8,fragCoord.xy)*0.005;

    vec4 color1 = mix(SunColor, SkyColor, 0.7);
    vec4 color2 = mix(SunColor, SkyColor, 0.8);
    vec4 color3 = mix(SunColor, SkyColor, 0.9);
    vec4 color4 = mix(SunColor, SkyColor, 1.0);
    vec4 color5 = SkyColor;

    float step0 = 0.0;
    float step1 = 0.2;
    float step2 = 0.4;
    float step3 = 0.6;
    float step4 = 0.8;
    float step5 = 1.0;

    vec4 color = mix(SunColor, color1, smoothstep(step0, step1, d));
    color = mix(color, color2, smoothstep(step1, step2, d));
    color = mix(color, color3, smoothstep(step2, step3, d));
    color = mix(color, color4, smoothstep(step3, step4, d));
    color = mix(color, color5, smoothstep(step4, step5, d));

    //These make the flat objects
    vec4 BackGround = SkyColor;
    vec4 Ground = GroundColor;
    vec4 Head = BodyColor;
    vec4 Body = ShirtColor;
    vec4 RightArm = BodyColor;
    vec4 LeftArm = BodyColor;
    vec4 RightLeg = PantsColor;
    vec4 LeftLeg= PantsColor;
    vec4 PantsMiddle = PantsColor;
    vec4 RightShoe = ShoeColor;
    vec4 LeftShoe = ShoeColor;
    vec4 House = HouseColor;
    vec4 Door = DoorColor;
    vec4 Window1 = DoorColor;
    vec4 Window2 = DoorColor;

    //These make all the shapes
    Ground *= step(length(uv.x - GroundLoc.x), GroundLength) *
        		step(length(uv.y - GroundLoc.y), GroundWidth); //This makes the ground

    float interp2 = step(Radius, length(uv-HeadLoc)); //This is responsible formaking the house

    Body *= step(length(uv.x - BodyLoc.x), BodyWidth) *
        	step(length(uv.y - BodyLoc.y), BodyHeight); //This makes the main body

    RightArm *= step(length(uv.x - RightArmLoc.x), ArmWidth) *
        		step(length(uv.y - RightArmLoc.y), ArmHeight);

    LeftArm *= step(length(uv.x - LeftArmLoc.x), ArmWidth) *
        		step(length(uv.y - LeftArmLoc.y), ArmHeight); //These are responisble for making the arms

    RightLeg *= step(length(uv.x - RightLegLoc.x), LegWidth) *
        		step(length(uv.y - RightLegLoc.y), LegHeight);

    LeftLeg *= step(length(uv.x - LeftLegLoc.x), LegWidth) *
        		step(length(uv.y - LeftLegLoc.y), LegHeight);

    PantsMiddle *= step(length(uv.x - PantsMiddleLoc.x), PantsWidth) *
        			step(length(uv.y - PantsMiddleLoc.y), PantsHeight); //These make the legs and the pants

    RightShoe *= step(length(uv.x - RightShoeLoc.x), ShoeWidth) *
        			step(length(uv.y - RightShoeLoc.y), ShoeHeight);

    LeftShoe *= step(length(uv.x - LeftShoeLoc.x), ShoeWidth) *
        			step(length(uv.y - LeftShoeLoc.y), ShoeHeight);

    House *= step(length(uv.x - HouseLoc.x), HouseLength) *
        		step(length(uv.y - HouseLoc.y), HouseHeight);

    Door *= step(length(uv.x - DoorLoc.x), DoorLength) *
        	step(length(uv.y - DoorLoc.y), DoorHeight);

    Window1 *= step(length(uv.x - Window1Loc.x), WindowLength) *
        		step(length(uv.y - Window1Loc.y), WindowHeight); 

    Window2 *= step(length(uv.x - Window2Loc.x), WindowLength) *
        		step(length(uv.y - Window2Loc.y), WindowHeight); //These four make the house in the background.


    vec4 Canvas = mix(BackGround, color, color.a);
    Canvas = mix(Head, Canvas, interp2);
    Canvas = mix(Canvas, Ground, Ground.a);
    Canvas = mix(Canvas, Body, Body.a);
    Canvas = mix(Canvas, RightArm, RightArm.a);
    Canvas = mix(Canvas, LeftArm, LeftArm.a);
    Canvas = mix(Canvas, RightLeg, RightLeg.a);
    Canvas = mix(Canvas, LeftLeg, LeftLeg.a);
    Canvas = mix(Canvas, PantsMiddle, PantsMiddle.a);
    Canvas = mix(Canvas, RightShoe, RightShoe.a);
    Canvas = mix(Canvas, LeftShoe, LeftShoe.a);
    Canvas = mix(Canvas, House, House.a);
    Canvas = mix(Canvas, Door, Door.a);
    Canvas = mix(Canvas, Window1, Window1.a);
    Canvas = mix(Canvas, Window2, Window2.a);

    fragColor = Canvas;
}
