//iChannel0: texture (e.g. Claude Van Damme)
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    const float CELL_SIZE = 55.0;
    const float ROUND_RADIUS = 0.53; // in [0, 1]

    #if 0
    // oval-splotches
    vec2 xy = fragCoord.xy  / iResolution.xy; // xy in [0, 1]
    vec2 s = (floor(xy * CELL_SIZE )) / CELL_SIZE; // sample bottom-left corner of CELL
    vec4 textureColor = texture(iChannel0, s);
    #else
    // circle-splotches
    vec2 xy = fragCoord.xy  / iResolution.y; 
    vec2 s = (floor(xy * CELL_SIZE )) / CELL_SIZE;
    vec2 T = iChannelResolution[0].xy; 
    vec4 textureColor = texture(iChannel0, s*T.y/T);
    #endif

	vec2 d = (xy - s) * CELL_SIZE - 0.5; // transform coordinates to unit circle

    if (length(d) < ROUND_RADIUS)
   		fragColor = textureColor;
   	else
        fragColor = vec4(13./256., 163./256., 38./256., 1.0);
}
