void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = 2.0*vec2(fragCoord.xy - 0.5*iResolution.xy)/iResolution.y;
    float maxX = iResolution.x / iResolution.y;
    vec3 bgCol = vec3(0.0); // black
	vec3 col1 = vec3(0.216, 0.471, 0.698); // blue
	vec3 col2 = vec3(1.00, 0.329, 0.298); // yellow
	vec3 col3 = vec3(0.867, 0.910, 0.247); // red
    vec3 pixel = bgCol;

    if (uv.x < -0.6 * maxX) {
    	pixel = vec3(step(-0.2, uv.y));
    }
    else if (uv.x < 0.2 * maxX) 
        pixel = vec3(step(-0.4, uv.y));
    else if (uv.x < 0.6 * maxX)
        pixel = 0.5 * vec3(step(0.1, uv.y));
    else
        pixel = vec3(step(0.5, uv.y));

    fragColor = vec4(pixel, 1.0);
}
