void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    vec4  tc=texture(iChannel0,uv);
    vec4 tcf=texture(iChannel0,uv-vec2(0.01,0.01));

    float t=0.5*sin(iTime);

    float r=tc.r;
    float g=tc.g;
    float b=tc.b;

    //Red has contribute 30%, Green has contributed 59%
    //which is greater in all three colors and Blue has contributed 11%.


    float greyc = ((0.30+t) * r) + ((0.59+t/2.0) * g) + ((0.11-t*2.) * b) ;  


	fragColor = vec4(greyc);
    fragColor += vec4(tcf)/greyc/5.;
}
