// Sunflower Transform Mosaic - written 2015 by JT
// (a modification of https://www.shadertoy.com/view/Mdd3R7)
// License Creative Commons Attribution-NonCommercial-ShareAlike 3.0 Unported License.
// Thanks to FabriceNeyret2 for optimizations
void mainImage( out vec4 O, vec2 I )
{
	vec2 R = iResolution.xy; 
    I = 42. * (I+I-R)/R.y;
    I = vec2(0, length(I)) + atan(I.y, I.x) / 6.283; // 2 * pi
    I.x = ceil(I.y) - I.x;
    I.x *= I.x * 2.47;//2.*(sqrt(5.)-1.); // looks nicest for mosaic-pattern: 4 * negative secondary golden ratio
    //I.x *= I.x * 2.4; // approx golden angle pi*(3-sqrt(5))
    //I.x *= I.x * 1.618;//(sqrt(5.)+1.)/2.; // golden ratio

    O = vec4(0); // FIX TO AVOID BLACK SCREEN ON WINDOWS
    O -= O + smoothstep(.4, .5, length(fract(I)-.5)) - 1.;
    I.y = 0.;
    O *= texture(iChannel0, ceil(I)*.01); // NOTE: filter set to linear instead of mipmap
}
