// variant of "helix 3b" https://shadertoy.com/view/XddfW8
//        and "helix 2c" https://www.shadertoy.com/view/4ddfW8

void mainImage(out vec4 O, vec2 U) {

    float t = iTime,l,a,d,v, r = 50., n = 3.82, // 24/2pi
          A = 2.38, H = 4.*cos(A/2.); // A=2.1
    mat2  R = mat2( sin(t+vec4(0,33,11,0)) );
    vec3  q = iResolution,
          D = vec3(.3*(U+U-q.xy)/q.y, -1),     // ray direction
          p = 30./q, c;                        // marching point along ray 
    O-=O;
    for ( O++; O.x > 0. && t > .01 ; O-=.015 )
        q = p,
        q.xz *= R, q.yz *= R,                  // rotation
   //   c = q, c.x+=r, a = atan(c.z,c.x), q.x = length(c.xz)-r, q.z = r*a,// global curvature
        l = length(q.xy), a = atan(q.y,q.x), d = a-q.z,
        d = min(v=abs( mod(d  ,6.28) -3.14), 
                  abs( mod(d-A,6.28) -3.14)),  // double strand (2pi/3)
        t = length(vec3(l-4.,d,fract(n*a)-.5)) - .3,  // spheres along double spring
        d = a-round(q.z*n)/n-A/2. -3.14 +.5/n,
        t = min(t, v=max(l-4.,(length(vec2((fract(q.z*n-.5)-.5)/n, l*cos(d)-H ))-.05)/n)),// rods
        p += t*D;                              // step forward = dist to obj

 // if (cos(a-q.z)<0.) O.gb *= .9; else O.rg *= .9;   // colored strands
    if (t==v) O.rg *= .9; else O.gb *= .9;       // colored rods vs spheres
}
