mat2 rotate2d(float _angle){
    return mat2(cos(_angle),-sin(_angle),
                sin(_angle),cos(_angle));
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 p = fragCoord/iResolution.xy;

    float i = 0.5;

    // dapples
    i += 0.5 * sin(0.8 * iTime + 15.0 * (p * rotate2d(0.6 + sin(iTime / 40.0))).x);
    i += 0.5 * sin(1.0 * iTime + 19.0 * (p * rotate2d(0.9)).x);
    i += 0.5 * sin(2.0 * iTime + 27.0 * (p * rotate2d(1.6)).x);
    i += 0.5 * sin(1.4 * iTime + 30.0 * (p * rotate2d(1.78)).x);

    // ripples
    vec2 p2 = p;
    p2.y *= 2.0;
    float r = distance(p2, vec2(0.5, 1.0));
    float ripple_intensity = pow(sin(iTime / 3.0 - r), 9.2);
    i += 0.15 * (1.0 / (r + 0.2)) * sin(-5.0 * iTime + 55.0 * r) * ripple_intensity;

    // low-frequency surge
    i += 0.85 * sin(iTime + 1.0 * (p * rotate2d(5.6)).x) * sin(iTime / 50.0);

    // coloring (i have no idea what this junk is doing)
    float k = clamp(0.2 + 0.8 * i, 0.0, 1.0);    
    vec3 color = vec3(1.0 - 1.9 * pow(k, 0.3), 1.0 - pow(k, 0.4), 1.0 - 0.6 * k);

    // Output to screen
    fragColor = vec4(color,1.0);
}
