// Code based in https://www.shadertoy.com/view/XsfGRn from IQ

vec2 translate2D(vec2 p, vec2 d) {
    return p - d;
}

#define MAX_LIVES 170
#define MAX_PER_LINE 17

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
	vec2 p2 = (20.0*fragCoord.xy-iResolution.xy)/min(iResolution.y,iResolution.x);

	p2 = translate2D(p2, vec2(0.0, 18.0));

	vec3 col = vec3(0.0);

    int hearts = MAX_LIVES;

    for(int i = 0; i < MAX_LIVES; i++) {
        float y = 0.0;
        float x = float(i);

        x = mod(float(i), float(MAX_PER_LINE));
        y = -floor(float(i / MAX_PER_LINE));

        if(i >= hearts) break;

        vec2 p = translate2D(p2, vec2(x*2.0, y*2.0));

        // shape
        float a = atan(p.x,p.y)/3.141593;
        float r = length(p);
        float h = abs(a);
        float d = (13.0*h - 22.0*h*h + 10.0*h*h*h)/(6.0-5.0*h);

        // color
        float s = 1.0-0.5*clamp(r/d,0.0,1.0);
        s = 0.75 + 0.75*p.x;
        s *= 1.0-0.25*r;
        s = 0.5 + 0.6*s;
        s *= 0.5+0.5*pow( 1.0-clamp(r/d, 0.0, 1.0 ), 0.1 );
        vec3 hcol = vec3(1.0,0.0,0.0)*s;

        col = mix( col, hcol, smoothstep( -0.01, 0.01, d-r) );
    }

    fragColor = vec4(col,1.0);
}
