// prev image is dezoomed and offseted
// new image blended on top is color-shifted and threaded by the high pow.

void mainImage( out vec4 O, vec2 U )
{
    U = U/iResolution.xy-.5;
    float t = iTime;
    O = mix( texture(iChannel0, U*1.015+.5 - 6e-3*sin(.3*t+vec2(0,1.6))),
             pow(.5+.5*sin(6.3*texture(iChannel1, U)+ t+vec4(0,2.1,-2.1,0)),vec4(20)),
           //pow(abs(sin(6.3*texture(iChannel1, U)+ t+vec4(0,2.1,-2.1,0))),vec4(20)),     // variant
           //.5+.5*sin(6.3*pow(texture(iChannel1, U),vec4(6))+ 10.*t+vec4(0,2.1,-2.1,0)), // variant
             .03 );

}
