#define WRAPPING_MODE
//Comment #define WRAPPING_MODE to disable it.
//Wrapping mode has a repeating pattern. Also LOOPS*9.
//Without it has no pattern but tiles can be seen.
#define LOOPS 100.
float div = 6.; //div^2 circles will appear on screen. The more, the faster but more tiles.
float size = 0.0013;
float min_sharp = 60.;
float max_sharp = 200.;

#ifdef WRAPPING_MODE
#define clamps(x) clamp(x,0.,1.)
float rand(float co){
    return fract(sin(dot(vec2(co) ,vec2(12.9898,78.233))) * 43758.5453);
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy;
    vec3 color1 = texture(iChannel0,uv).rgb;
    for (float i = 0.; i < LOOPS; i++) {
        vec2 position = vec2(rand(iTime+(i/3.5)),rand(iTime+20.+(i*1.2)));
        float alpha = 0.;
        for(float j=0.;j<9.;j++){
            vec2 pos2 = vec2(mod(j,3.)-1.,floor(j/3.)-1.);
            alpha += 1.-clamps((length(fract(uv*div)-(position+pos2))-(size/div))*(mix(min_sharp,max_sharp,rand(i+iTime+.123))/div));
        }
        vec3 color2 = texture(iChannel1,(position/div)+(floor(((uv*div)-position)+.5)/div)).rgb;
       	color1 = mix(color1,color2,clamps(alpha));
    }
    fragColor = vec4(color1,1.0);
}
#else
