float getState(vec2 pos) {
	return texture(iChannel1, pos).r > 0.2 ? 1.0 : 0.0;   
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{      
    vec2 st = fragCoord.st/iResolution.st;

    vec2 texel = 1.0/iResolution.xy;

    vec2 offset[9];

    offset[0] = vec2(-texel.x, -texel.y);
    offset[1] = vec2(     0.0, -texel.y);
    offset[2] = vec2( texel.x, -texel.y);
    offset[3] = vec2(-texel.x,      0.0);
    offset[4] = vec2(     0.0,      0.0);
    offset[5] = vec2( texel.x,      0.0);
    offset[6] = vec2(-texel.x,  texel.y);
    offset[7] = vec2(     0.0,  texel.y);
    offset[8] = vec2( texel.x,  texel.y);

    float sum = 0.0;

    float state = getState(st);
	float times_alive = texture(iChannel1, st).g;
    float consecutive_times_alive = texture(iChannel1, st).b;

    for (int i = 0; i < 9; i++){
        sum += getState(st + offset[i]);
    }

    sum -= state;
    float next = state;

    if ( state == 1.0 ) {
        if (( sum < 2.0 ) || ( sum > 3.0 )) {
            next = 0.0;
        } else if (( sum == 2.0 ) || ( sum == 3.0 )) {
         	next = 1.0;   
        }
    } else {
        if ( sum == 3.0 ) {
        	next = 1.0;   
        }
    }

    float d = distance(st, vec2(0.5));
    if ( d < mod(iTime * 0.01 + 0.001, 0.7) && d > mod(iTime * 0.01 - 0.001, 0.7) ) {
   		next = 1.0;
    }

    if ( next == 1.0 ) {
    	times_alive += 1.0;   
    }

    if( state == next && state == 1.0) {
    	consecutive_times_alive += 1.0;   
    } else {
        consecutive_times_alive = 0.0;
    }


    times_alive *= 0.98;
    vec4 color = vec4(0.0);


    color = vec4(next, times_alive, state, 1.0);   


    fragColor = color;
}
