renderpass.code:
#define sf(x)     smoothstep( .1, .9 -step(.4,abs(x-.5)) , x-step(.9,x) )
#define sfract(x) sf(fract(x))
#define sfloor(x) ( x-.233 -.533*sfract(x) )    // ( x-.5 - .533*(sfrac-.5) )
#define sround(x) sfloor(x+.5)
#define smod(x,n) (sfract((x)/(n))*(n))


void mainImage( out vec4 O, vec2 U )
{
    vec2 R = iResolution.xy;
    U *= 8./R;

    O = vec4(   sfract(U.x) < U.y 
             && U.y < sfloor(U.x) );
    if (fract(U.x) < 8./R.x ) O = vec4(1,0,0,1);
    if (fract(U.y) < 8./R.y ) O = vec4(0,1,0,1);

 }
