#define PI 3.14159265359
#define EXP 2.71828182846



#define BAD_3

#ifdef BAD_1
float w1 = 0.5;
float w2 = 1.5;
float w3 = 20.0;
float threshold = 0.99;
#endif
#ifdef BAD_2
float w1 = 1.0;
float w2 = 5.0;
float w3 = 50.0;
float threshold = 0.95;
#endif
#ifdef BAD_3
float w1 = 3.0;
float w2 = 3.0;
float w3 = 20.0;
float threshold = 0.80;
#endif

float horizontal(in vec2 xy, float t)	{
    float v = cos(w1*xy.x + t);
	return v;
}

float diagonal(in vec2 xy, float t)	{
    float v = cos(w2*(xy.x*cos(t) + 5.0*xy.y*sin(t)) + t);
    return v;
}
float radial(in vec2 xy, float t)	{
    float x = 0.3*xy.x - 0.5 + cos(t);
    float y = 0.3*xy.y - 0.5 + sin(t*0.5);
    float v = sin(w3*sqrt(x*x+y*y+1.0)+t);
    return v;
}

float map(float a,float b,float c,float d,float x) {
    return ((x-a)*(d-c)/(b-a))+c;
}

float log_map(float a,float b,float c,float d,float x) {
    float x1 = map(a,b,1.0,EXP,x);
    return log(x1)*(d-c)+c;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )	{
    float t = iTime;
	vec2 xy = fragCoord.xy / iResolution.xy;
    float v = horizontal(xy,t);
    v += diagonal(xy,t);
    v += radial(xy,t);
    v /= 3.0;
    float r = step(map(-1.0,1.0,   0.75,1.0,sin(PI*v)), threshold);
    fragColor = vec4(1,1,1,1)-vec4(r,r,r,0.0);
}
