#define PI 3.1415926
#define Epsilon 0.0001

// rotation
vec2 r(vec2 v, float y){
    return cos(y)*v + sin(y)*vec2(-v.y, v.x);
}

float usdCircle(vec2 p, vec2 cpos){
	return clamp(0.0, 1.0, 1.0/length(p-cpos));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 p = vec2(fragCoord.xy / iResolution.xy - 0.5);
    p.x *= iResolution.x / iResolution.y;
    p.xy *= 40.0;
    //q.y -= 0.25;
    fragColor = vec4(1.0, 0.5, 0.0, 1.0);
    float dis = 0.0;
    for (int i = 0; i < 8; i++){
        vec2 cpos = vec2(cos(PI*0.25*float(i)), sin(PI*0.25*float(i)));
        cpos.xy *= 14.5 *(sin(iTime*PI*0.5 + PI*0.25*float(i))+1.0)/2.0;

        dis += usdCircle(p, cpos);
    }
    vec3 color = vec3(1.0, 1.0, 1.0);
    if (dis/8.0 > 0.125){
    	color = vec3(0.0,0.0,0.0);
    }
    // contrast
    color = smoothstep(0.0, 1.0, color); 
    fragColor = vec4(color, 1.0);
}
