void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;


    // Time varying pixel color
    vec3 col = 0.5 + 0.35*(iTime * 10.0 + uv.xyx + vec3(0,2,4));
    vec4 noise = texture(iChannel1, 0.5*(uv + (vec2(0.0, col.x) * 0.2)));
    vec4 color = texture(iChannel0, uv + (noise.xy * 0.1));
    // Output to screen
    fragColor = color;
}
